import React, { Component } from 'react';
import axios from 'axios';

const BASE_URL = "https://my-json-server.typicode.com/themeland/sapp/themeOneFeatureSection";

class FeatureSection extends Component {
    state = {
        data: {"preHeading": "Premium",
        "preHeadingspan": "Features",
        "heading": "What Makes sApp Different?",
        "headingTwo": "Explore Our Features",
        "headingThree": "Why Small Business Owner's Loves sApp",
        "headingFour": "Why choose sApp?",
        "headingText": "",
        "headingTexttwo": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Laborum obcaecati.",
        "featureThumb": "/img/features_thumb.png",
        "featureData": [
          {
            "id": 1,
            "image": "/img/featured_image_1.png",
            "title": "Online Courses",
            "text": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Veritatis culpa expedita dignissimos."
          },
          {
            "id": 2,
            "image": "/img/featured_image_2.png",
            "title": "Live Chat",
            "text": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Veritatis culpa expedita dignissimos."
          },
          {
            "id": 3,
            "image": "/img/featured_image_3.png",
            "title": "Secure Data",
            "text": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Veritatis culpa expedita dignissimos."
          }
        ],
        "featureData_1": [
          {
            "id": 1,
            "image": "/img/featured_image_4.png",
            "title": "Easy to Customize",
            "text": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Veritatis culpa expedita dignissimos."
          },
          {
            "id": 2,
            "image": "/img/featured_image_5.png",
            "title": "Responsive Design",
            "text": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Veritatis culpa expedita dignissimos."
          },
          {
            "id": 3,
            "image": "/img/featured_image_6.png",
            "title": "Help Documentation",
            "text": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Veritatis culpa expedita dignissimos."
          }
        ],
        "featureDataTwo": [
          {
            "id": 1,
            "iconClass": "flaticon-tap",
            "title": "Online Courses",
            "text": ""
          },
          {
            "id": 2,
            "iconClass": "flaticon-fingerprint",
            "title": "Fingerprint Data",
            "text": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Veritatis culpa expedita dignissimos."
          },
          {
            "id": 3,
            "iconClass": "flaticon-notification",
            "title": "Tap Notification",
            "text": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Veritatis culpa expedita dignissimos."
          },
          {
            "id": 4,
            "iconClass": "flaticon-place",
            "title": "Location Tracking",
            "text": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Veritatis culpa expedita dignissimos."
          },
          {
            "id": 5,
            "iconClass": "flaticon-settings",
            "title": "Powerful Settings",
            "text": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Veritatis culpa expedita dignissimos."
          },
          {
            "id": 6,
            "iconClass": "flaticon-language",
            "title": "Multiple Language",
            "text": "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Veritatis culpa expedita dignissimos."
          }
        ]},
        featureDataTwo: [{
            "id": 1,
            "iconClass": "flaticon-exams",
            "title": "Online Courses",
            "text": ""
          },
          {
            "id": 2,
            "iconClass": "flaticon-fingerprint",
            "title": "Live Classes",
            "text": ""
          },
          {
            "id": 3,
            "iconClass": "flaticon-notification",
            "title": "Online Exams",
            "text": ""
          },
          {
            "id": 4,
            "iconClass": "flaticon-place",
            "title": "Personalized Contents.",
            "text": ""
          },
          {
            "id": 5,
            "iconClass": "flaticon-settings",
            "title": "Reports",
            "text": ""
          },
          {
            "id": 6,
            "iconClass": "flaticon-language",
            "title": "Performance Analytics",
            "text": ""
          }]
    }

    render() {
        return (
            <section id="features" className="section features-area ptb_100">
                <div className="container">
                    <div className="row justify-content-center">
                    <div className="col-12 col-md-10 col-lg-6">
                        {/* Section Heading */}
                        <div className="section-heading text-center">
                        <h2>{this.state.data.headingTwo}</h2>
                        <p className="d-none d-sm-block mt-4">{this.state.data.headingText}</p>
                        <p className="d-block d-sm-none mt-4">{this.state.data.headingTexttwo}</p>
                        </div>
                    </div>
                    </div>
                    <div className="row">
                    {this.state.featureDataTwo.map((item, idx) => {
                        return(
                            <div key={`ft_${idx}`} className="col-12 col-md-6 col-lg-4">
                                {/* Icon Box */}
                                <div className="icon-box text-center p-4">
                                {/* Featured Icon */}
                                <div className="featured-icon mb-3">
                                    <span className={item.iconClass} />
                                </div>
                                {/* Icon Text */}
                                <div className="icon-text">
                                    <h3 className="mb-2">{item.title}</h3>
                                    <p>{item.text}</p>
                                </div>
                                </div>
                            </div>
                        );
                    })}
                    </div>
                </div>
            </section>
        );
    }
}

export default FeatureSection;
